CREATE OR REPLACE FUNCTION obter_cargo_usuario ( nm_usuario_p text, IE_OPCAO_P text default 'D') RETURNS varchar AS $body$
DECLARE



cd_pessoa_fisica_w	varchar(255) := '';
ds_cargo_W		varchar(255) := '';

BEGIN

select	coalesce(cd_pessoa_fisica,'0')
into STRICT	cd_pessoa_fisica_w
from	usuario
where 	nm_usuario = nm_usuario_p;

if cd_pessoa_fisica_w <> '0' then
	ds_cargo_W		:= OBTER_CARGO_PF(cd_pessoa_fisica_w,coalesce(IE_OPCAO_P,'D'));
end if;

return	ds_cargo_w;

end;
$body$
