SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cartao_fidelidade_pf ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_cartao_w	varchar(60);
nr_sequencia_w	bigint;

/* ie_opcao_p  C - Código,  D - Descrição */

C01 CURSOR FOR
SELECT	nr_seq_cartao
from	pf_cartao_fidelidade
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	dt_inicio_validade	< clock_timestamp()
and	coalesce(dt_fim_validade,clock_timestamp() + interval '1 days') > clock_timestamp()
order by dt_inicio_validade desc;



BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	OPEN C01;
	LOOP
	FETCH C01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		nr_sequencia_w	:= nr_sequencia_w;
	END LOOP;
	close c01;
end if;

ds_cartao_w	:= to_char(nr_sequencia_w);

if (nr_sequencia_w > 0) and (ie_opcao_p = 'D') then
	select	ds_cartao
	into STRICT	ds_cartao_w
	from	cartao_fidelidade
	where	nr_sequencia	= nr_sequencia_w;
end if;

return	ds_cartao_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
