SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_carteira_pf (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


nr_doc_convenio_w	varchar(255);
cd_unimed_w 		varchar(255);
nr_seq_w		bigint;
nr_atendimento_w 	bigint;


BEGIN


select	max(nr_atendimento)
into STRICT	nr_atendimento_w
from	atendimento_paciente
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

select	max(nr_doc_convenio)
into STRICT	nr_doc_convenio_w
from	atend_categoria_convenio
where	nr_atendimento  = nr_atendimento_w
and	dt_inicio_vigencia = (	SELECT	max(dt_inicio_vigencia)
				from	atend_categoria_convenio
				where	nr_atendimento = nr_atendimento_w);

if (coalesce(nr_doc_convenio_w::text, '') = '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_w
	from	pf_codigo_externo
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
        and     coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento
	and	ie_tipo_codigo_externo	= 'GPL';

	select	max(cd_pessoa_fisica_externo),
		max(cd_usuario_convenio)
	into STRICT	cd_unimed_w,
		nr_doc_convenio_w
	from	pf_codigo_externo
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	nr_sequencia		= nr_seq_w
	and	ie_tipo_codigo_externo	= 'GPL';

	nr_doc_convenio_w	:= cd_unimed_w || nr_doc_convenio_w;
end if;

return nr_doc_convenio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
