SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_carteira_res_atendimento ( NR_ATENDIMENTO_P bigint ) RETURNS varchar AS $body$
DECLARE


DS_RETORNO_W        varchar(200) := '0';
IE_ATENDIMENTO      varchar(2) := 'N';


BEGIN

SELECT count(cd_externo)
INTO STRICT IE_ATENDIMENTO
FROM CONVERSAO_MEIO_EXTERNO
WHERE NM_TABELA = 'ATEND_CATEGORIA_CONVENIO'
AND NM_ATRIBUTO = 'NR_SEQUENCIA'
AND CD_INTERNO = 'CSPAR'
and CD_EXTERNO = 'S';

IF (NR_ATENDIMENTO_P IS NOT NULL AND NR_ATENDIMENTO_P::text <> '') THEN

    IF (IE_ATENDIMENTO > 0) THEN
        SELECT coalesce(CD_USUARIO_CONVENIO, '0')
        into STRICT DS_RETORNO_W
        from ATEND_CATEGORIA_CONVENIO
        where nr_atendimento = NR_ATENDIMENTO_P;
    END IF;

    IF (DS_RETORNO_W = '0') THEN

      BEGIN
        SELECT PLS_OBTER_CARTEIRA_BENEF(NR_SEQUENCIA)
        INTO STRICT DS_RETORNO_W
        FROM (
            SELECT MAX(NR_SEQUENCIA)  NR_SEQUENCIA
            FROM PLS_SEGURADO 
            WHERE CD_PESSOA_FISICA = OBTER_PESSOA_ATENDIMENTO(NR_ATENDIMENTO_P, 'C')
        ) alias4;
      EXCEPTION WHEN OTHERS THEN
        DS_RETORNO_W := NULL;
      END;
    END IF;
END IF;


RETURN DS_RETORNO_W;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_carteira_res_atendimento ( NR_ATENDIMENTO_P bigint ) FROM PUBLIC;

