CREATE OR REPLACE FUNCTION obter_caso_teste_exec_plano ( nr_sequencia_p bigint, ie_separador_p text) RETURNS varchar AS $body$
DECLARE


ds_caso_teste_w		varchar(255);
ds_retorno_w		varchar(4000);

C01 CURSOR FOR
	SELECT	a.ds_caso_teste
	from	teste_soft_exec_acao a
	where	(a.nr_seq_caso_teste IS NOT NULL AND a.nr_seq_caso_teste::text <> '')
	and	a.nr_seq_teste_software = nr_sequencia_p
	group by a.nr_seq_caso_teste,
		a.ds_caso_teste
	order by 1;


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) and (coalesce(ie_separador_p,'X') <> 'X') then
	open C01;
	loop
	fetch C01 into
		ds_caso_teste_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w := ds_caso_teste_w;
		else
			ds_retorno_w := substr(ds_retorno_w || ie_separador_p || ds_caso_teste_w,1,4000);
		end if;
		end;
	end loop;
	close C01;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
