CREATE OR REPLACE FUNCTION obter_categoria_convenio_atend (nr_atendimento_p bigint, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(30);


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') then
	begin
	select  substr(max(obter_categoria_convenio(cd_convenio_p,a.cd_categoria)),1,30)
	into STRICT 	ds_retorno_w
	from 	atend_categoria_convenio a,
			categoria_convenio b
	where	a.cd_convenio			= b.cd_convenio
	and     a.nr_atendimento		= nr_atendimento_p
	and 	a.cd_convenio			= cd_convenio_p;
	end;
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
