CREATE OR REPLACE FUNCTION obter_categoria_convenio ( CD_CONVENIO_P bigint, cd_categoria_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);


BEGIN
if (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') and (cd_categoria_p IS NOT NULL AND cd_categoria_p::text <> '') then
	begin
	select	ds_categoria
	into STRICT	ds_retorno_w
	from	categoria_convenio
	where	cd_convenio	= cd_convenio_p
	  and	cd_categoria	= cd_categoria_p;
	end;
end if;

RETURN ds_retorno_w;
END;
