CREATE OR REPLACE FUNCTION obter_categoria_conv_tit_rec (nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE

 
ds_retorno_w		bigint;
nr_interno_conta_w	bigint;	
		 

BEGIN 
 
select	max(nr_interno_conta) 
into STRICT	nr_interno_conta_w 
from	titulo_receber 
where	nr_titulo	= nr_titulo_p;
 
ds_retorno_w	:= null;
if (nr_interno_conta_w IS NOT NULL AND nr_interno_conta_w::text <> '') then 
 
	select	cd_categoria_parametro 
	into STRICT	ds_retorno_w 
	from	conta_paciente 
	where	nr_interno_conta	= nr_interno_conta_w;
 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
