CREATE OR REPLACE FUNCTION obter_cbo_saude (nr_seq_cbo_p bigint) RETURNS varchar AS $body$
DECLARE

/** Retorna a descrição do CBO
 *  Juliane Menin - 25/10/2007
*/
ds_retorno_w	varchar(80);


BEGIN
if (nr_seq_cbo_p IS NOT NULL AND nr_seq_cbo_p::text <> '') then
	select	substr(ds_cbo,1,80)
	into STRICT	ds_retorno_w
	from	cbo_saude
	where	nr_sequencia = nr_seq_cbo_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
