CREATE OR REPLACE FUNCTION obter_cc_local_solic ( nr_solic_compra_p bigint) RETURNS bigint AS $body$
DECLARE

 
cd_centro_custo_w			integer;
cd_centro_custo_local_w			integer;


BEGIN 
 
select	a.cd_centro_custo, 
	b.cd_centro_custo 
into STRICT	cd_centro_custo_w, 
	cd_centro_custo_local_w 
from	solic_compra a, 
	local_estoque b 
where	a.nr_solic_compra = nr_solic_compra_p 
and	b.cd_local_estoque = a.cd_local_estoque;
 
if (coalesce(cd_centro_custo_w::text, '') = '') then 
	cd_centro_custo_w	:= cd_centro_custo_local_w;
end if;
 
return	cd_centro_custo_w;
 
end;
