SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cc_materiais_pendentes (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_prescricao_w	bigint;
ds_retorno_w	varchar(1);
ie_alta_materiais_w	varchar(1);


BEGIN

ie_alta_materiais_w := Obter_param_Usuario(3111, 234, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_alta_materiais_w);

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	max(a.nr_prescricao)
	into STRICT	nr_prescricao_w
	from	prescr_medica a,
			prescr_material b
	where	a.nr_prescricao = b.nr_prescricao
	and		coalesce(b.cd_motivo_baixa,0)	= 0
	and		b.ie_suspenso			= 'N'
	and		coalesce(a.dt_suspensao::text, '') = ''
	and		a.nr_atendimento = nr_atendimento_p
	and (exists (SELECT 	1
			        from  	cirurgia x
				where 	x.nr_prescricao 	= a.nr_prescricao
				and	x.nr_atendimento 	= a.nr_atendimento) or
			exists (	select 	1
				from  	cirurgia y
				where 	y.nr_cirurgia  	= a.nr_cirurgia
				and	y.nr_atendimento 	= a.nr_atendimento
				and	ie_alta_materiais_w 	= 'A'));

	if (nr_prescricao_w > 0) then
		ds_retorno_w	:= 'S';
	else
		ds_retorno_w	:= 'N';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_cc_materiais_pendentes (nr_atendimento_p bigint) FROM PUBLIC;

