CREATE OR REPLACE FUNCTION obter_cd_darf_variacao ( cd_tributo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_variacao_w		varchar(2);
ie_periodicidade_w		varchar(1);
nr_sequencia_w		bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	tributo_dctf
where	cd_tributo = cd_tributo_p;

select	cd_variacao,
	ie_periodicidade
into STRICT	cd_variacao_w,
	ie_periodicidade_w
from	tributo_dctf
where	nr_sequencia = nr_sequencia_w;

if (ie_opcao_p = 'V') then
	return	cd_variacao_w;
elsif (ie_opcao_p = 'P') then
	return 	ie_periodicidade_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
