CREATE OR REPLACE FUNCTION obter_cd_ds_convenio_conta (nr_interno_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
 
ds_convenio_w	varchar(255)	:= '';

--Opção 
-- CD - Códido do Convênio 
--DS - Descrição do convenio 
 

BEGIN 
  if (ie_opcao_p = 'DS') then 
	select	max(b.ds_convenio) 
	into STRICT	ds_convenio_w 
	from	convenio b, 
		conta_paciente a 
	where	a.cd_convenio_parametro	= b.cd_convenio 
	and	a.nr_interno_conta	= nr_interno_conta_p;
  end if;
  
  if (ie_opcao_p = 'CD') then 
	select	max(b.CD_convenio) 
	into STRICT	ds_convenio_w 
	from	convenio b, 
		conta_paciente a 
	where	a.cd_convenio_parametro	= b.cd_convenio 
	and	a.nr_interno_conta	= nr_interno_conta_p;
  end if;
  
 
return	ds_convenio_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
