CREATE OR REPLACE FUNCTION obter_cd_estagio_autor_atend ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE



qt_autor_pend_w		integer;
qt_autor_just_w		integer;
qt_autor_enca_w		integer;
qt_autor_pend_canc_w	integer;
qt_autor_repr_w		integer;
qt_autor_canc_w		integer;
cd_retorno_w		integer	:=0;
qt_autorizacao_w	integer;



BEGIN

SELECT	COUNT(*)
INTO STRICT	qt_autorizacao_w
FROM	autorizacao_convenio
WHERE	nr_atendimento	= nr_atendimento_p;

IF (qt_autorizacao_w 	> 0) THEN

	SELECT	COUNT(*)
	INTO STRICT	qt_autor_pend_w
	FROM	autorizacao_convenio
	WHERE	nr_atendimento	= nr_atendimento_p
	AND		obter_estagio_autor(nr_seq_estagio, 'C') IN (1,2);

	IF (qt_autor_pend_w > 0) THEN
		cd_retorno_w	:= 1; -- Wheb_mensagem_pck.get_texto(309527); --'Pendência de autorização';
		GOTO Final;
	END IF;

	SELECT	COUNT(*)
	INTO STRICT	qt_autor_just_w
	FROM	autorizacao_convenio
	WHERE	nr_atendimento	= nr_atendimento_p
	AND		obter_estagio_autor(nr_seq_estagio, 'C') IN (3);

	IF (qt_autor_just_w > 0) THEN
		cd_retorno_w	:= 3; -- Wheb_mensagem_pck.get_texto(309530); --'Justificado';
	GOTO Final;
	END IF;

	SELECT	COUNT(*)
	INTO STRICT	qt_autor_enca_w
	FROM	autorizacao_convenio
	WHERE	nr_atendimento	= nr_atendimento_p
	AND	obter_estagio_autor(nr_seq_estagio, 'C') IN (5);

	IF (qt_autor_enca_w > 0) THEN
		cd_retorno_w	:= 5; -- Wheb_mensagem_pck.get_texto(309532); --'Encaminhado';
	GOTO Final;

	END IF;


	SELECT	COUNT(*)
	INTO STRICT	qt_autor_pend_canc_w
	FROM	autorizacao_convenio
	WHERE	nr_atendimento	= nr_atendimento_p
	AND 	obter_estagio_autor(nr_seq_estagio, 'C') IN (60);

	IF (qt_autor_pend_canc_w > 0) THEN
		cd_retorno_w	:= 60; --Wheb_mensagem_pck.get_texto(309533); --'Pendência de cancelamento';
	GOTO Final;
	END IF;



	SELECT	COUNT(*)
	INTO STRICT	qt_autor_repr_w
	FROM	autorizacao_convenio
	WHERE	nr_atendimento	= nr_atendimento_p
	AND		obter_estagio_autor(nr_seq_estagio, 'C') IN (90);

	IF (qt_autor_repr_w > 0) THEN
		cd_retorno_w	:= 90; --Wheb_mensagem_pck.get_texto(309535); --'Reprovado';
	GOTO Final;
	END IF;



	SELECT	COUNT(*)
	INTO STRICT	qt_autor_canc_w
	FROM	autorizacao_convenio
	WHERE	nr_atendimento	= nr_atendimento_p
	AND		obter_estagio_autor(nr_seq_estagio, 'C') IN (70);

	IF (qt_autor_canc_w > 0) THEN
		cd_retorno_w	:= 70;--Wheb_mensagem_pck.get_texto(309536); --'Cancelado';
	ELSE
		cd_retorno_w	:= 10;--Wheb_mensagem_pck.get_texto(309537); --'Autorizado';
	END IF;

END IF;

<<Final>>

RETURN	cd_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
