SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cd_func_etapa_proj ( NR_SEQ_PROJ_CRON_ETAPA_P bigint) RETURNS NUMERIC AS $body$
DECLARE

  PR_RETORNO_W    NUMERIC;
  DS_ATIVIDADE_W  varchar(255);

BEGIN
  BEGIN
    SELECT OS.CD_FUNCAO
      INTO STRICT PR_RETORNO_W
      FROM MAN_ORDEM_SERVICO OS
     WHERE OS.NR_SEQUENCIA IN (SELECT OBTER_OS_STATUS_GERAL_PROJ(NR_SEQ_PROJ_CRON_ETAPA_P) );
  EXCEPTION
    WHEN no_data_found THEN
      PR_RETORNO_W := NULL;
    WHEN OTHERS THEN
      PR_RETORNO_W := NULL;
  END;

 IF coalesce(PR_RETORNO_W::text, '') = '' THEN
   BEGIN
      SELECT A.DS_ATIVIDADE
        INTO STRICT DS_ATIVIDADE_W
        FROM PROJ_CRON_ETAPA A
       WHERE A.NR_SEQUENCIA = NR_SEQ_PROJ_CRON_ETAPA_P;
    EXCEPTION
      WHEN no_data_found THEN
        DS_ATIVIDADE_W := NULL;
      WHEN OTHERS THEN
        DS_ATIVIDADE_W := NULL;
    END;

    BEGIN
      SELECT FH.CD_FUNCAO
        INTO STRICT PR_RETORNO_W
        FROM FUNCOES_HTML5 FH,
             FUNCAO F
       WHERE FH.CD_FUNCAO        = F.CD_FUNCAO
         AND trim(both UPPER(OBTER_DESC_EXPRESSAO_IDIOMA(CD_EXP_FUNCAO, DS_FUNCAO, 5))) = trim(both UPPER(DS_ATIVIDADE_W));
    EXCEPTION
      WHEN no_data_found THEN
        PR_RETORNO_W := NULL;
      WHEN OTHERS THEN
        PR_RETORNO_W := NULL;
    END;
  END IF;

  IF coalesce(PR_RETORNO_W::text, '') = '' THEN
    BEGIN
      SELECT FH.CD_FUNCAO
        INTO STRICT PR_RETORNO_W
        FROM FUNCOES_HTML5 FH,
             FUNCAO F
       WHERE FH.CD_FUNCAO        = F.CD_FUNCAO
         AND trim(both UPPER(OBTER_DESC_EXPRESSAO_IDIOMA(CD_EXP_FUNCAO, DS_FUNCAO, 5))) = trim(both UPPER(DS_ATIVIDADE_W))
       AND (FH.IE_STATUS IS NOT NULL AND FH.IE_STATUS::text <> '')  LIMIT 1;
    EXCEPTION
      WHEN no_data_found THEN
        PR_RETORNO_W := NULL;
      WHEN OTHERS THEN
        PR_RETORNO_W := NULL;
    END;
  END IF;

  RETURN PR_RETORNO_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
