CREATE OR REPLACE FUNCTION obter_cd_imp_classe_mat ( ds_classe_material_p text) RETURNS bigint AS $body$
DECLARE

  ds_retorno_w bigint;

BEGIN
    IF (ds_classe_material_p IS NOT NULL AND ds_classe_material_p::text <> '') THEN 
      SELECT cd_classe_material 
      INTO STRICT   ds_retorno_w 
      FROM   imp_classe_material 
      WHERE  tasyautocomplete.Toindexwithoutaccents(ds_classe_material) LIKE '%' 
                                                                             || 
             Upper( 
                    Elimina_acentuacao(ds_classe_material_p)) 
