SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cd_intevalo_classif ( cd_intervalo_p text, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_intervalo_prescr_w	varchar(1);
cd_intervalo_w		varchar(7) := '';
cd_intervalo_agora_w	varchar(7);
ie_prescr_retrograda_w	varchar(1);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	begin

	select	coalesce(max(ie_prescr_emergencia),'N')
	into STRICT	ie_prescr_retrograda_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;

	select	max(cd_intervalo)
	into STRICT	cd_intervalo_agora_w
	from	intervalo_prescricao
	where	ie_agora 	= 'S'
	--and	nvl(ie_se_farmacia_amb,'N') = 'N'
	and	obter_se_intervalo(cd_intervalo, 15) = 'S'
	and	((coalesce(ie_so_retrograda,'N') = 'N') or (ie_prescr_retrograda_w = 'N'))
	and	cd_intervalo	= obter_menor_intervalo_agora(1, nr_prescricao_p)
	and	ie_situacao 	= 'A';

	if (cd_intervalo_agora_w IS NOT NULL AND cd_intervalo_agora_w::text <> '') then
		begin

		select	coalesce(max('S'),'N')
		into STRICT	ie_intervalo_prescr_w
		from	intervalo_prescricao
		where	cd_intervalo 	= cd_intervalo_p
		and	ie_agora	= 'S';

		if (coalesce(cd_intervalo_p::text, '') = '') or (ie_intervalo_prescr_w = 'N') then
			begin
			cd_intervalo_w	:= cd_intervalo_agora_w;
			end;
		end if;
		end;
	end if;
	end;
end if;
return	cd_intervalo_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
