CREATE OR REPLACE FUNCTION obter_cd_mat_gener_padron ( cd_estabelecimento_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


cd_mat_generico_w		bigint:= 0;
ie_padronizado_w		varchar(1) := 'N';


BEGIN

if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

		select	coalesce(max(ie_padronizado),'N')
		into STRICT	ie_padronizado_w
		from	material_estab
		where	cd_material 			= cd_material_p
		and		cd_estabelecimento		= cd_estabelecimento_p
		and		coalesce(ie_prescricao,'S')	= 'S';

		if (coalesce(ie_padronizado_w,'N') = 'S') then
			cd_mat_generico_w := cd_material_p;
		end if;

end if;

return cd_mat_generico_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
