CREATE OR REPLACE FUNCTION obter_cd_mot_cancel_agenda (nr_seq_agenda_p bigint, cd_agenda_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_motivo_w		agenda_consulta.cd_motivo_cancelamento%type;
cd_tipo_agenda_w	bigint;
ds_retorno_w	varchar(255);


BEGIN
if (coalesce(nr_seq_agenda_p, 0) <> 0 and coalesce(cd_agenda_p,0) <> 0) then

        select  max(cd_tipo_agenda)
	into STRICT	cd_tipo_agenda_w
	from	agenda
	where	cd_agenda = cd_agenda_p;

	if (cd_tipo_agenda_w IS NOT NULL AND cd_tipo_agenda_w::text <> '')then
		if (cd_tipo_agenda_w = 3)then
			select	coalesce(max(cd_motivo_cancelamento),'N')
			into STRICT	cd_motivo_w
			from	agenda_consulta
			where	nr_sequencia = nr_seq_agenda_p;
			if (cd_motivo_w <> 'N') then
				select	max(ds_motivo)
				into STRICT	ds_retorno_w
				from	agenda_motivo_cancelamento
				where	cd_motivo = cd_motivo_w
				and     cd_estabelecimento = cd_estabelecimento_p;
			end if;
		end if;
		if (cd_tipo_agenda_w = 2)then
			select	coalesce(max(cd_motivo_cancelamento),'N')
			into STRICT	cd_motivo_w
			from	agenda_paciente
			where	nr_sequencia = nr_seq_agenda_p;
			if (cd_motivo_w <> 'N') then
				select	max(ds_motivo)
				into STRICT	ds_retorno_w
				from	agenda_motivo_cancelamento
				where	cd_motivo = cd_motivo_w
				and     cd_estabelecimento = cd_estabelecimento_p;
			end if;
		end if;
	end if;

	select	coalesce(max(cd_motivo_cancelamento),'N')
	into STRICT	cd_motivo_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p;
	if (cd_motivo_w <> 'N') then
		select	max(ds_motivo)
		into STRICT	ds_retorno_w
		from	agenda_motivo_cancelamento
		where	cd_motivo = cd_motivo_w;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
