CREATE OR REPLACE FUNCTION obter_cd_pessoa_externo (cd_pessoa_fisica_p text, cd_estabelecimento_p bigint DEFAULT NULL) RETURNS varchar AS $body$
DECLARE

    ds_retorno_w varchar(255);


BEGIN

    SELECT
    MAX(cd_pessoa_fisica_externo)
    INTO STRICT ds_retorno_w
    FROM
    pf_codigo_externo
    WHERE    cd_pessoa_fisica = cd_pessoa_fisica_p
    AND coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = coalesce(cd_estabelecimento_p, wheb_usuario_pck.get_cd_estabelecimento);

    RETURN ds_retorno_w;

END;
$body$
