CREATE OR REPLACE FUNCTION obter_cd_pessoa_responsavel (nr_seq_gerencia_p bigint) RETURNS bigint AS $body$
DECLARE

cd_pessoa_resp_w	varchar(10);

/*
  *	Function para obter o cd_pessoa_fisica do respons√°vel da tabela DEPTO_GERENCIA_PHILIPS
  */
BEGIN
select	max(a.cd_pessoa_fisica)
into STRICT	cd_pessoa_resp_w
from	pessoa_fisica a
where	a.nm_pessoa_fisica = (obter_resp_gerencia_wheb(nr_seq_gerencia_p));

return	cd_pessoa_resp_w;

end;
