CREATE OR REPLACE FUNCTION obter_cd_pessoa_substituta ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

cd_pessoa_substituta_w	varchar(10);

/*
  *	Function para obter o cd_pessoa_fisia da pessoa substituta do responsÃ¡vel
  */
BEGIN

select	max(a.cd_pessoa_substituta)
into STRICT	cd_pessoa_substituta_w
from	pessoa_fisica_delegacao a
where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
and	ie_objetivo  = 'AV'
and	trunc(dt_limite) >= trunc(clock_timestamp());

return	cd_pessoa_substituta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
