CREATE OR REPLACE FUNCTION obter_cd_protocolo_pac_atend (nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_protocolo_w	varchar(255);


BEGIN
if (coalesce(nr_seq_atendimento_p,0) > 0) then
	SELECT	MAX(a.cd_protocolo)
	INTO STRICT    ds_protocolo_w
	FROM	paciente_setor a,
		paciente_atendimento b
	WHERE	a.nr_seq_paciente	= b.nr_seq_paciente
