SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cd_severidade_vv ( NR_ORDEM_SERV_P bigint) RETURNS varchar AS $body$
DECLARE

  PR_RETORNO_W  varchar(100) := '';

BEGIN

  BEGIN
    SELECT CASE WHEN IE_SEVERIDADE = '5' THEN '1'
                WHEN IE_SEVERIDADE = '4' THEN '2'
                WHEN IE_SEVERIDADE = '2' THEN '4'
                WHEN IE_SEVERIDADE = '1' THEN '5'
                ELSE IE_SEVERIDADE
           END
      INTO STRICT PR_RETORNO_W
      FROM MAN_ORDEM_SERV_ANALISE_VV  VV
     WHERE VV.NR_SEQ_ORDEM_SERV = NR_ORDEM_SERV_P
       AND VV.NR_SEQUENCIA IN (SELECT MAX(VV_SUB.NR_SEQUENCIA)
                                 FROM MAN_ORDEM_SERV_ANALISE_VV  VV_SUB
                                WHERE VV.NR_SEQ_ORDEM_SERV = VV_SUB.NR_SEQ_ORDEM_SERV)  LIMIT 1;
  EXCEPTION
    WHEN no_data_found THEN
      PR_RETORNO_W := NULL;
    WHEN OTHERS THEN
      PR_RETORNO_W := NULL;
  END;

  IF coalesce(PR_RETORNO_W::text, '') = '' THEN
    BEGIN
      SELECT CASE WHEN OS.NR_SEQ_SEVERIDADE_WHEB = 2 THEN 1
                  WHEN OS.NR_SEQ_SEVERIDADE_WHEB = 1 THEN 2
                  ELSE NR_SEQ_SEVERIDADE_WHEB
             END
        INTO STRICT PR_RETORNO_W
        FROM MAN_ORDEM_SERVICO OS
       WHERE OS.NR_SEQUENCIA = NR_ORDEM_SERV_P  LIMIT 1;
    EXCEPTION
      WHEN no_data_found THEN
        PR_RETORNO_W := NULL;
      WHEN OTHERS THEN
        PR_RETORNO_W := NULL;
    END;
  END IF;

  IF coalesce(PR_RETORNO_W::text, '') = '' THEN
    BEGIN
      SELECT CASE WHEN OS.NR_SEQ_SEVERIDADE = 2 THEN 1
                  WHEN OS.NR_SEQ_SEVERIDADE = 1 THEN 2
                  ELSE NR_SEQ_SEVERIDADE
             END
        INTO STRICT PR_RETORNO_W
        FROM MAN_ORDEM_SERVICO OS
       WHERE OS.NR_SEQUENCIA = NR_ORDEM_SERV_P  LIMIT 1;
    EXCEPTION
      WHEN no_data_found THEN
        PR_RETORNO_W := NULL;
      WHEN OTHERS THEN
        PR_RETORNO_W := NULL;
    END;
  END IF;

  RETURN PR_RETORNO_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
