CREATE OR REPLACE FUNCTION obter_cd_sistema_ant (cd_tributo_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


CD_SISTEMA_ANT_W			varchar(14);



BEGIN

select coalesce(max(j.CD_SISTEMA_ANT),0)
into STRICT CD_SISTEMA_ANT_W
from TRIBUTO_CONTA_PAGAR a,
PESSOA_JURIDICA_ESTAB j
where j.CD_CGC = a.CD_BENEFICIARIO
and a.CD_TRIBUTO = cd_tributo_p
and a.NR_SEQUENCIA = nr_sequencia_p;

return CD_SISTEMA_ANT_W;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
