SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cd_solucao ( nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1000);
ds_solucao_w			varchar(255);
cd_estabelecimento_w		bigint;
cd_material_w			integer;
qt_comp_atual_w			integer;
ie_parametro_w			varchar(15);
ds_material_w			varchar(1000);

C02 CURSOR FOR  --Componentes da solução
SELECT	a.cd_material
from	prescr_material a,
	prescr_solucao b
where	b.nr_seq_solucao = a.nr_sequencia_solucao
and	b.nr_prescricao = a.nr_prescricao
and	b.nr_prescricao = nr_prescricao_p
and	b.nr_seq_solucao = nr_seq_solucao_p;


BEGIN

select 	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

select	coalesce(max(ie_solucao_continua),'X') --Parâmetro
into STRICT	ie_parametro_w
from	parametro_medico
where	cd_estabelecimento = cd_estabelecimento_w;

select	count(*) --Quantidade de componentes da solução atual
into STRICT	qt_comp_atual_w
from	prescr_material
where	nr_prescricao = nr_prescricao_p
and	nr_sequencia_solucao = nr_seq_solucao_p;

select	ds_solucao
into STRICT	ds_solucao_w
from	prescr_solucao
where	nr_prescricao = nr_prescricao_p
and	nr_seq_solucao = nr_seq_solucao_p;

open C02;
loop
fetch C02 into
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	ds_material_w := ds_material_w||cd_material_w||',';
	end;
end loop;
close C02;

if (ie_parametro_w = 'A') then --Titulo e componentes
	ds_retorno_w := ds_solucao_w||ds_material_w||qt_comp_atual_w;
elsif (ie_parametro_w = 'C') or (ie_parametro_w = 'CDVF') then --Componentes
	ds_retorno_w := ds_material_w||qt_comp_atual_w;
elsif (ie_parametro_w = 'T') then --Titulo
	ds_retorno_w := ds_solucao_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_cd_solucao ( nr_prescricao_p bigint, nr_seq_solucao_p bigint) FROM PUBLIC;

