CREATE OR REPLACE FUNCTION obter_cd_status_processo_pa (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE



nr_seq_status_proc_w status_pa_atend_log.nr_seq_status_proc%type;
nr_seq_processo_w status_pa_atend_log.nr_seq_status%type;
cd_retorno_w PA_STATUS_PROCESSO_PAC.nr_seq_processo%type;
							

BEGIN

SELECT max(b.nr_seq_status_proc),
	   max(b.nr_seq_status)
INTO STRICT   nr_seq_status_proc_w,
	   nr_seq_processo_w
FROM   status_pa_atend_log b
where b.nr_sequencia = (SELECT max(a.nr_sequencia)
from status_pa_atend_log a
WHERE  a.nr_atendimento = nr_atendimento_p);


IF (nr_seq_processo_w IS NOT NULL AND nr_seq_processo_w::text <> '') then

		select max(nr_sequencia)
		into STRICT cd_retorno_w
		from PA_STATUS_PROCESSO_PAC
		where nr_sequencia = nr_seq_status_proc_w;

	IF (coalesce(cd_retorno_w::text, '') = '') then
	
		select max(nr_sequencia)
		into STRICT cd_retorno_w
		from PA_STATUS_PROCESSO_PAC
		where nr_sequencia = nr_seq_status_proc_w
		AND IE_STATUS_INICIAL = 'S';

	end if;
else
		select max(b.nr_sequencia)
		into STRICT cd_retorno_w
		from PA_STATUS_PACIENTE a,
		PA_STATUS_PROCESSO_PAC b
		where a.nr_sequencia = b.nr_seq_processo
		and a.IE_STATUS_INICIAL = 'S'
		and b.IE_STATUS_INICIAL = 'S';
		
end if;

RETURN	cd_retorno_w;

END;
