CREATE OR REPLACE FUNCTION obter_cd_uso_cfdi ( nr_sequencia_p bigint ) RETURNS varchar AS $body$
DECLARE


retorno_w		varchar(255);


BEGIN
select  coalesce(b.cd_uso_cfdi,'P01') cd_uso_cfdi
INTO STRICT retorno_w
FROM nota_fiscal a
LEFT OUTER JOIN fis_uso_cfdi b ON (a.nr_sequencia = b.nr_seq_nota)
WHERE a.nr_sequencia = nr_sequencia_p;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
