CREATE OR REPLACE FUNCTION obter_cd_usuario_conv_glosa2 ( nr_atendimento_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


cd_usuario_ibcm_w		varchar(60);
cd_usuario_ipe_w		varchar(60);

/*
ie_opcao_p
1 - IBCM
2 - IPE
*/
BEGIN
select	coalesce(max(cd_usuario_conv_glosa),0),
	coalesce(max(cd_usuario_convenio),0)
into STRICT	cd_usuario_ibcm_w,
	cd_usuario_ipe_w
from	atend_categoria_convenio
where	nr_atendimento 	= nr_atendimento_p
and 	dt_inicio_vigencia	= (	SELECT max(x.dt_inicio_vigencia)
					from atend_categoria_convenio x
					where x.nr_atendimento	= nr_atendimento_p);

if (ie_opcao_p = 1) then
	return 	cd_usuario_ibcm_w;
else
	return 	cd_usuario_ipe_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
