CREATE OR REPLACE FUNCTION obter_cd_valor_fiscal (nr_seq_var_fis_p bigint) RETURNS bigint AS $body$
DECLARE



cd_valor_fiscal_w 	bigint := 0;
qt_registros_w		bigint;

BEGIN


select count(*)
into STRICT	qt_registros_w
from	fis_variacao_fiscal
where 	nr_sequencia = coalesce(nr_seq_var_fis_p,0);

if (qt_registros_w > 0) then

	select 	cd_codigo_valores_fiscais
	into STRICT 	cd_valor_fiscal_w
	from	fis_variacao_fiscal
	where	nr_sequencia = nr_seq_var_fis_p;

end if;

return	cd_valor_fiscal_w;

end;
$body$
