CREATE OR REPLACE FUNCTION obter_centro_custo_ref_cc ( cd_empresa_p parametros_conta_contabil.cd_empresa%type, cd_centro_custo_ref_p parametros_conta_contabil.cd_centro_custo%type) RETURNS bigint AS $body$
DECLARE


cd_centro_custo_w	parametros_conta_contabil.cd_centro_custo%type;


BEGIN
	select 	max(c.cd_centro_custo)
	into STRICT	cd_centro_custo_w
	from	centro_custo c,
		estabelecimento e
	where	e.cd_estabelecimento = c.cd_estabelecimento
	and	e.ie_tipo_estab = 'M'
	and	e.cd_empresa = cd_empresa_p
	and	c.cd_centro_custo_ref = cd_centro_custo_ref_p;

return	cd_centro_custo_w;

end;
$body$
