CREATE OR REPLACE FUNCTION obter_centro_custo_setor ( cd_setor_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_resultado_w		varchar(100);
cd_centro_custo_w		bigint;



BEGIN

select	cd_centro_custo
into STRICT	cd_centro_custo_w
from	setor_atendimento
where	cd_setor_atendimento = cd_setor_atendimento_p;

if (cd_centro_custo_w IS NOT NULL AND cd_centro_custo_w::text <> '') then

	select ds_centro_custo
	into STRICT ds_resultado_w
	from centro_custo
	where cd_centro_custo = cd_centro_custo_w;
end if;

if (ie_opcao_p = 'C') then
	ds_resultado_w := cd_centro_custo_w;
end if;

return ds_resultado_w;
end;
$body$
