SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_centro_custo_titulo ( nr_titulo_p bigint, ie_tipo_titulo_p text) RETURNS varchar AS $body$
DECLARE


/* ie_tipo_titulo_p

R	Receber
P	Pagar

*/
ds_retorno_w		varchar(2000);
ds_centro_custo_w	varchar(85);

c01 CURSOR FOR
SELECT	substr(b.ds_centro_custo,1,80)
from	centro_custo b,
	titulo_receber_classif a
where	a.cd_centro_custo	= b.cd_centro_custo
and	a.nr_titulo		= nr_titulo_p
group by b.ds_centro_custo;


BEGIN

if (ie_tipo_titulo_p	= 'R') then

	open	c01;
	loop
	fetch	c01 into
		ds_centro_custo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		if (coalesce(ds_retorno_w::text, '') = '') or (length(ds_retorno_w) < 1920) then

			if (coalesce(ds_retorno_w::text, '') = '') then
				ds_retorno_w	:= ds_centro_custo_w;
			else
				ds_retorno_w	:= ds_retorno_w || ', ' || ds_centro_custo_w;
			end if;

		end if;

	end	loop;
	close	c01;

end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_centro_custo_titulo ( nr_titulo_p bigint, ie_tipo_titulo_p text) FROM PUBLIC;

