SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cep_mascara ( cd_cep_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter o campo CEP com máscara
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w    varchar(255);
cd_cep_w        varchar(50);


BEGIN

if (cd_cep_p IS NOT NULL AND cd_cep_p::text <> '') then
	cd_cep_w := obter_somente_numero(cd_cep_p);
	select	CASE WHEN coalesce(cd_cep_p::text, '') = '' THEN null  ELSE replace(replace(replace(to_char(lpad(replace(cd_cep_w, ' ', ''),8,'0'),'00,000,000'),',','-'), ' ', '')			,'-'||trim(both to_char(trunc(mod(lpad(cd_cep_w,8,'0'),1000000)/1000),'000'))			,'.'||trim(both to_char(trunc(mod(lpad(cd_cep_w,8,'0'),1000000)/1000),'000'))) END
	into STRICT	ds_retorno_w
	;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
