CREATE OR REPLACE FUNCTION obter_cep_municipio_res (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE



cd_municipio_ibge_w pessoa_fisica.cd_municipio_ibge%type;
nr_cep_cidade_nasc_w pessoa_fisica.nr_cep_cidade_nasc%type;
ds_retorno_w varchar(255);


BEGIN


select max(coalesce(cd_municipio_ibge,0))
into STRICT cd_municipio_ibge_w
from pessoa_fisica
where cd_pessoa_fisica = cd_pessoa_fisica_p;


if (cd_municipio_ibge_w > 0) then

	select to_char(max(cd_municipio_ibge_w || calcula_digito('modulo10',cd_municipio_ibge_w)))
	into STRICT ds_retorno_w
	;

else

	select max(nr_cep_cidade_nasc)
	into STRICT nr_cep_cidade_nasc_w
	from pessoa_fisica
	where cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (nr_cep_cidade_nasc_w IS NOT NULL AND nr_cep_cidade_nasc_w::text <> '') then

		select to_char(max(obter_municipio_ibge(nr_cep_cidade_nasc_w) ||  calcula_digito('modulo10', obter_municipio_ibge(nr_cep_cidade_nasc_w))))
		into STRICT ds_retorno_w
		;

	end if;

end if;

	return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
