CREATE OR REPLACE FUNCTION obter_cgc_origem_destino_nf ( nr_seq_nf_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_operacao_nf_w		nota_fiscal.cd_operacao_nf%type;
ds_retorno_w			estabelecimento.cd_cgc%type;
ie_transferencia_estab_w	operacao_nota.ie_transferencia_estab%type;
cd_cgc_origem_w			estabelecimento.cd_cgc%type;
cd_cgc_destino_w		estabelecimento.cd_cgc%type;


/*

This funciton was create for the integration 101883 - Philips Standard Sending - Stock Movement - DONT USE THIS FUNCTION

IE_OPCAO_P

O - Origem
D - Destinatario

*/
BEGIN

select 	cd_operacao_nf,
	substr(cd_cgc_emitente,1,14) cd_cgc_origem,
	substr(CASE WHEN coalesce(cd_cgc::text, '') = '' THEN null  ELSE CASE WHEN substr(obter_se_nota_entrada_saida(nr_sequencia),1,1)='E' THEN  substr(OBTER_CGC_ESTABELECIMENTO(cd_estabelecimento),1,14)  ELSE cd_cgc END  END ,1,14) cd_cgc_destinatario
into STRICT	cd_operacao_nf_w,
	cd_cgc_origem_w,
	cd_cgc_destino_w
from  	nota_fiscal
where 	nr_sequencia = nr_seq_nf_p;


select	ie_transferencia_estab
into STRICT 	ie_transferencia_estab_w
from 	operacao_nota
where 	cd_operacao_nf = cd_operacao_nf_w;



	if (ie_transferencia_estab_w = 'S') then

		if (ie_opcao_p = 'O') then

			ds_retorno_w :=	cd_cgc_origem_w;

		else

			ds_retorno_w :=	cd_cgc_destino_w;
		end if;

	else

		ds_retorno_w	:= '';

	end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
