CREATE OR REPLACE FUNCTION obter_cgc_venc_cotacao ( nr_cot_compra_p bigint, nr_item_cot_compra_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_cgc_fornecedor_w			varchar(14);

C01 CURSOR FOR 
    SELECT C.CD_CGC_FORNECEDOR 
    FROM  COT_COMPRA_FORN_ITEM_TR_V C 
	 where C.NR_COT_COMPRA   = NR_COT_COMPRA_P 
	  and c.nr_item_cot_compra = nr_item_cot_compra_p 
    ORDER BY vl_presente desc, C.QT_PRIORIDADE;

BEGIN
 
select coalesce(max(cd_cgc_fornecedor_venc_alt),'0') 
into STRICT	cd_cgc_fornecedor_w 
from	cot_compra_item 
where	nr_cot_compra		= nr_cot_compra_p 
 and	nr_item_cot_compra	= nr_item_cot_compra_p;
 
if (cd_cgc_fornecedor_w = '0') then 
	OPEN C01;
	LOOP 
	FETCH C01 into 
		CD_CGC_FORNECEDOR_W;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		CD_CGC_FORNECEDOR_W	:= CD_CGC_FORNECEDOR_W;
		end;
	END LOOP;
	CLOSE C01;
end if;
RETURN cd_cgc_fornecedor_w;
END;
$body$
