CREATE OR REPLACE FUNCTION obter_checagem_quimioterapia ( nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(255);
ie_checado_w		varchar(5);
qt_checado_w		bigint	:= 0;
qt_pendente_w		bigint	:= 0;
qt_medic_w		bigint	:= 0;

c01 CURSOR FOR
	SELECT	coalesce(ie_checado,'N')
	from	paciente_atend_medic
	where	nr_seq_atendimento	= nr_seq_atendimento_p
	and	coalesce(nr_seq_diluicao::text, '') = '';


BEGIN

--Verifica o total de medicamentos para o paciente
select	count(*)
into STRICT	qt_medic_w
from	paciente_atend_medic
where	nr_seq_atendimento	= nr_seq_atendimento_p
and	coalesce(nr_seq_diluicao::text, '') = '';

--Conta cada um dos medicamentos e agrupa por chegado e não checado
open c01;
loop
fetch C01 into
	ie_checado_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	--Conta a administração pelos status dos medicamentos
	if (ie_checado_w = 'S') then
		qt_checado_w	:= qt_checado_w + 1;
	else
		qt_pendente_w	:= qt_pendente_w + 1;
	end if;
	end;
end loop;
close c01;

--Verifica as quantidades para retornar
if (qt_medic_w = 0) then
     ds_retorno_w    := wheb_mensagem_pck.get_texto(308053); -- Sem medicação informada
elsif (qt_checado_w = qt_medic_w) then
     ds_retorno_w    := wheb_mensagem_pck.get_texto(308075); -- Checado
elsif (qt_pendente_w = qt_medic_w) then
     ds_retorno_w    := wheb_mensagem_pck.get_texto(308076); -- Pendente de checagem
else
     ds_retorno_w    := wheb_mensagem_pck.get_texto(308078); -- Checado parcialmente
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_checagem_quimioterapia ( nr_seq_atendimento_p bigint) FROM PUBLIC;

