CREATE OR REPLACE FUNCTION obter_checkup_paciente ( nm_tabela_p text, cd_pessoa_fisica_p text, nr_seq_checkup_etapa_p bigint) RETURNS bigint AS $body$
DECLARE


cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;



BEGIN

if (nm_tabela_p = 'CHECKUP_ETAPA') then
	select max(a.cd_pessoa_fisica)
	into STRICT cd_pessoa_fisica_w
	from checkup a, checkup_etapa b
	where a.nr_sequencia = b.nr_seq_checkup
	and b.nr_sequencia = nr_seq_checkup_etapa_p;
	
	return cd_pessoa_fisica_w;
else
	return cd_pessoa_fisica_p;
end if;

end;
$body$
LANGUAGE PLPGSQL
