SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cheque_bordero (nr_bordero_p bigint) RETURNS varchar AS $body$
DECLARE


nr_cheque_w		varchar(20);
ds_cheques_w		varchar(254);

c01 CURSOR FOR
	SELECT	CASE WHEN coalesce(a.nr_cheque::text, '') = '' THEN ''  ELSE a.nr_cheque || ', ' END
	from	cheque b,
		CHEQUE_BORDERO_TITULO a
	WHERE	a.nr_seq_cheque	= b.nr_sequencia
	and	coalesce(b.dt_cancelamento::text, '') = ''
	and	a.nr_bordero = nr_bordero_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_cheque_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_cheques_w := substr(ds_cheques_w || nr_cheque_w,1,254);
end loop;
close c01;

return	ds_cheques_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
