CREATE OR REPLACE FUNCTION obter_cheque_titulo_pagar (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


nr_cheque_w		varchar(20);
ds_cheques_w		varchar(254);

c01 CURSOR FOR
	SELECT	distinct a.nr_cheque
	from	cheque b,
		CHEQUE_BORDERO_TITULO a
	where	a.nr_seq_cheque	= b.nr_sequencia
	and	coalesce(b.dt_cancelamento::text, '') = ''
	and	a.nr_titulo	= nr_titulo_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_cheque_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (length(ds_cheques_w || nr_cheque_w) < 250) then
		if (ds_cheques_w IS NOT NULL AND ds_cheques_w::text <> '') then
			ds_cheques_w := ds_cheques_w || ',';
		end if;
		ds_cheques_w := substr(ds_cheques_w || nr_cheque_w,1,254);
	end if;

end loop;
close c01;

return	substr(ds_cheques_w,1,254);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
