CREATE OR REPLACE FUNCTION obter_cheque_tit_receber (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


nr_cheque_w		varchar(20);
ds_cheques_w		varchar(254);

c01 CURSOR FOR
	SELECT	CASE WHEN coalesce(nr_cheque::text, '') = '' THEN ''  ELSE nr_cheque || ', ' END
	from	cheque_cr
	where	nr_titulo	= nr_titulo_p;

BEGIN

open c01;
loop
fetch c01 into
	nr_cheque_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_cheques_w := substr(ds_cheques_w || nr_cheque_w,1,254);
end loop;
close c01;

return	ds_cheques_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
