SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ciap_atendimento ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);

cd_ciap_w		ciap_atendimento.cd_ciap%type;
ds_ciap_w		varchar(255);
ie_opcao_w		varchar(1);

BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	ie_opcao_w	:= coalesce(upper(ie_opcao_p),'C');

	select max(cd_ciap)
	into STRICT   cd_ciap_w
	from   ciap_atendimento
	where  nr_atendimento = nr_atendimento_p;


	if (ie_opcao_w = 'C') then
		ds_retorno_w := to_char(cd_ciap_w);
	elsif (ie_opcao_w = 'D') then
		select	substr(ds_ciap,1,255)
		into STRICT	ds_ciap_w
		from	problema_ciap
		where	cd_ciap = cd_ciap_w
		and	ie_situacao = 'A';

	   ds_retorno_w := ds_ciap_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
