CREATE OR REPLACE FUNCTION obter_ciclos_tratamento_onc (NR_SEQ_PACIENTE_P bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(4000);
ie_protocolo_livre_w	varchar(10);
NR_CICLOS_w				bigint;


BEGIN


select	coalesce(max(NR_CICLOS),1)
into STRICT		NR_CICLOS_w
from	paciente_setor
where	NR_SEQ_PACIENTE = NR_SEQ_PACIENTE_P;


for i in 1..NR_CICLOS_w loop
	begin
	ds_retorno_w	:= ds_retorno_w||'C'||i||',';
	end;
end loop;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	ds_retorno_w	:= substr(ds_retorno_w,1,length(ds_retorno_w)-1);
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
