CREATE OR REPLACE FUNCTION obter_cid10_envio_laudo ( nr_seq_interno_p bigint, nr_cid10_p bigint) RETURNS varchar AS $body$
DECLARE



cd_doenca_cid_w		varchar(10);
ds_retorno_w		varchar(10);
qt_cid10_w		bigint	:= 0;


C01 CURSOR FOR
	SELECT	cd_doenca_cid
	from	sus_laudo_area_irradiada
	where	nr_seq_laudo	= nr_seq_interno_p
	order by nr_sequencia;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
		cd_doenca_cid_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	qt_cid10_w	:= qt_cid10_w + 1;
	if (nr_cid10_p	= 1) and (qt_cid10_w	= 1) then
		ds_retorno_w	:= cd_doenca_cid_w;
	elsif (nr_cid10_p	= 2) and (qt_cid10_w	= 2) then
		ds_retorno_w	:= cd_doenca_cid_w;
	elsif (nr_cid10_p	= 3) and (qt_cid10_w	= 3) then
		ds_retorno_w	:= cd_doenca_cid_w;
	end if;
END LOOP;
Close C01;

return ds_retorno_w;

END;
$body$
