SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cid10_paciente ( cd_pessoa_fisica_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

			 
nr_atendimento_w 	bigint;
cd_CID10_1_w		varchar(10);
dt_CID10_1_w		timestamp;
cd_CID10_2_w		varchar(10);
dt_CID10_2_w		timestamp;

BEGIN
--Busca Último Atendimento 
SELECT	MAX(coalesce(nr_atendimento,0)) 
into STRICT	nr_atendimento_w 
FROM	atendimento_paciente 
WHERE	cd_pessoa_fisica = cd_pessoa_fisica_p;
 
if (nr_atendimento_w > 0) then 
 
	--Buscar Opção 1: Diagnóstico PEP 
	SELECT	MAX(dt_liberacao) 
	INTO STRICT	dt_CID10_1_w 
	FROM	diagnostico_doenca 
	WHERE	nr_atendimento = nr_atendimento_w 
	AND	LENGTH(cd_doenca) > 0 
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
	AND	ie_situacao = 'A' 
	ORDER BY dt_liberacao DESC;
 
	SELECT	max(coalesce(cd_doenca,'')) 
	into STRICT	cd_CID10_1_w 
	FROM	diagnostico_doenca 
	WHERE	nr_atendimento = nr_atendimento_w 
	AND	LENGTH(cd_doenca) > 0 
	AND	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
	AND	ie_situacao = 'A' 
	AND	dt_liberacao = dt_CID10_1_w;
 
 
	--Buscar Opção 2: Diagnóstico do Tumor PEP e Loco Regional PEP 
	SELECT	MAX(dt_liberacao) 
	INTO STRICT	dt_CID10_2_w 
	FROM	can_loco_regional 
	WHERE	nr_atendimento = nr_atendimento_w 
	AND	LENGTH(cd_doenca_cid) > 0 
	AND	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
	AND	ie_situacao = 'A' 
	ORDER BY dt_liberacao DESC;
 
	SELECT	max(coalesce(cd_doenca_cid,'')) 
	into STRICT	cd_CID10_2_w 
	FROM	can_loco_regional 
	WHERE	nr_atendimento = nr_atendimento_w 
	AND	LENGTH(cd_doenca_cid) > 0 
	AND	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
	AND	ie_situacao = 'A' 
	AND	dt_liberacao = dt_CID10_2_w;
 
	if length(coalesce(cd_CID10_1_w,'')) > 0 then 
		return 	cd_CID10_1_w;
	else 
		return	cd_CID10_2_w;
	end if;	
else 
	return '';
end if;
	 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
