CREATE OR REPLACE FUNCTION obter_cids_atendimento (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_doenca_w	diagnostico_doenca.cd_doenca%type;
ds_cid_w	varchar(255);

C01 CURSOR FOR
	SELECT	a.cd_doenca
	from	diagnostico_doenca a
	where	nr_atendimento	= nr_atendimento_p;


BEGIN

begin
	OPEN C01;
	LOOP
	FETCH C01 into
		cd_doenca_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		BEGIN

		if (coalesce(ds_cid_w::text, '') = '') then
			ds_cid_w := ds_cid_w || cd_doenca_w;

		else
			ds_cid_w := ds_cid_w || ',' || cd_doenca_w;

		end if;

		END;
	END LOOP;
	CLOSE C01;
end;
RETURN ds_cid_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
