CREATE OR REPLACE FUNCTION obter_cid_atendimento (nr_atendimento_p bigint, ie_classificacao_p text) RETURNS varchar AS $body$
DECLARE


/* tipo do diagnostico   1-Provisório 2-Definitivo */

/* classificação do cid	 P-Principal S-Secundário */

cd_doenca_w			varchar(10);
dt_diagnostico_w		timestamp;

C01 CURSOR FOR
	SELECT	a.dt_diagnostico
	from	diagnostico_medico a
	where	a.nr_atendimento	= nr_atendimento_p
	and	exists (SELECT	1
		from	diagnostico_doenca x
		where	x.nr_atendimento	= a.nr_atendimento
		and		x.dt_diagnostico	= a.dt_diagnostico
		and		coalesce(x.ie_situacao,'A') = 'A')
	order by	ie_tipo_diagnostico desc, dt_diagnostico desc;


BEGIN
cd_doenca_w	:= '';
/* busca diagnostico medico */

begin
	OPEN C01;
	LOOP
	FETCH C01 into
		dt_diagnostico_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		BEGIN
		begin
		select		max(cd_doenca)
		into STRICT		cd_doenca_w
		from		diagnostico_doenca
		where		nr_atendimento		= nr_atendimento_p
		and			dt_diagnostico		= dt_diagnostico_w
		and			ie_classificacao_doenca	= ie_classificacao_p;
		exception
				when others then
				cd_doenca_w	:= '';
		end;
		if (cd_doenca_w IS NOT NULL AND cd_doenca_w::text <> '') then
			exit;
		end if;
		END;
	END LOOP;
	CLOSE C01;
end;
RETURN cd_doenca_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
