CREATE OR REPLACE FUNCTION obter_cid_doenca_procedimento ( cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
cd_doenca_cid_w	varchar(10);
ds_doenca_cid_w	varchar(240);


BEGIN

if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then
	begin
	select	cd_doenca_cid,
		substr(obter_desc_cid_doenca(cd_doenca_cid),1,240)
	into STRICT	cd_doenca_cid_w,
		ds_doenca_cid_w
	from	procedimento
	where	cd_procedimento 	= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p;

	if (ie_opcao_p = 'C') then
		ds_retorno_w	:= cd_doenca_cid_w;
	elsif (ie_opcao_p = 'D') then
		ds_retorno_w	:= ds_doenca_cid_w;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
