CREATE OR REPLACE FUNCTION obter_cid_pf ( cd_pessoa_fisica_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
nr_atendimento_w	bigint;
cd_doenca_w		varchar(10);
ds_cid_w		varchar(300);


BEGIN 
 
select	coalesce(max(a.nr_atendimento),0) 
into STRICT	nr_atendimento_w 
from	atendimento_paciente a 
where	a.cd_pessoa_fisica	=	cd_pessoa_fisica_p 
and	exists (	SELECT	1 
			from	diagnostico_doenca b 
			where	b.nr_atendimento	=	a.nr_atendimento);
		 
select	max(cd_doenca) 
into STRICT	cd_doenca_w 
from	diagnostico_doenca 
where	nr_atendimento	=	nr_atendimento_w;
 
select	obter_desc_cid(cd_doenca_w) 
into STRICT	ds_cid_w
;
 
if (ie_opcao_p = 'C') then 
	return	cd_doenca_w;
else 
	return	ds_cid_w;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
